package AllAssignment;

public class Assignment12_From_1to_100_printEven_and_Odd_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i =1;i<=100;i++) {
	if(i%2==0) {
		System.out.println("The number is even is =" +i);
	}

	else {
		System.out.println("Then number is odd= " +i);
	}
}
	}

}
